// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "hardhat/console.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "../storage/UuidCreator.sol";

library Bounty {
  bytes32 constant BOUNTY_KEY = keccak256(abi.encode("penx.io.storage.bounty"));

  bytes32 public constant BOUNTY_ID_KEY = keccak256("BOUNTY_ID_KEY");

  using EnumerableSet for EnumerableSet.UintSet;

  struct Store {
    EnumerableSet.UintSet requestIds;
    mapping(uint256 => Request) requests;
  }

  struct Request {
    address recipient;
    string bountyId;
  }

  function load() public pure returns (Store storage self) {
    bytes32 s = BOUNTY_KEY;
    assembly {
      self.slot := s
    }
  }

  function create(address recipient, string memory bountyId) external returns (uint256) {
    Store storage self = load();

    uint256 requestId = UuidCreator.nextId(BOUNTY_ID_KEY);

    self.requests[requestId].bountyId = bountyId;
    self.requests[requestId].recipient = recipient;

    self.requestIds.add(requestId);

    return requestId;
  }

  function get(uint256 requestId) external view returns (Request memory) {
    Store storage self = load();
    return self.requests[requestId];
  }

  function getIds() external view returns (uint256[] memory) {
    Store storage self = load();
    return self.requestIds.values();
  }

  function remove(uint256 requestId) external {
    Store storage self = load();

    self.requestIds.remove(requestId);
    delete self.requests[requestId];
  }
}

contracts/storage/LibDiamond.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

/******************************************************************************\
* Author: Nick Mudge <nick@perfectabstractions.com> (https://twitter.com/mudgen)
* EIP-2535 Diamonds: https://eips.ethereum.org/EIPS/eip-2535
/******************************************************************************/
import { IDiamond } from "../interfaces/IDiamond.sol";
import { IDiamondCut } from "../interfaces/IDiamondCut.sol";

// Remember to add the loupe functions from DiamondLoupeFacet to the diamond.
// The loupe functions are required by the EIP2535 Diamonds standard

error NoSelectorsGivenToAdd();
error NotContractOwner(address _user, address _contractOwner);
error NoSelectorsProvidedForFacetForCut(address _facetAddress);
error CannotAddSelectorsToZeroAddress(bytes4[] _selectors);
error NoBytecodeAtAddress(address _contractAddress, string _message);
error IncorrectFacetCutAction(uint8 _action);
error CannotAddFunctionToDiamondThatAlreadyExists(bytes4 _selector);
error CannotReplaceFunctionsFromFacetWithZeroAddress(bytes4[] _selectors);
error CannotReplaceImmutableFunction(bytes4 _selector);
error CannotReplaceFunctionWithTheSameFunctionFromTheSameFacet(bytes4 _selector);
error CannotReplaceFunctionThatDoesNotExists(bytes4 _selector);
error RemoveFacetAddressMustBeZeroAddress(address _facetAddress);
error CannotRemoveFunctionThatDoesNotExist(bytes4 _selector);
error CannotRemoveImmutableFunction(bytes4 _selector);
error InitializationFunctionReverted(address _initializationContractAddress, bytes _calldata);

library LibDiamond {
    bytes32 constant DIAMOND_STORAGE_POSITION = keccak256("diamond.standard.diamond.storage");

    struct FacetAddressAndSelectorPosition {
        address facetAddress;
        uint16 selectorPosition;
    }

    struct DiamondStorage {
        // function selector => facet address and selector position in selectors array
        mapping(bytes4 => FacetAddressAndSelectorPosition) facetAddressAndSelectorPosition;
        bytes4[] selectors;
        mapping(bytes4 => bool) supportedInterfaces;
        address contractOwner;
    }

    function diamondStorage() internal pure returns (DiamondStorage storage ds) {
        bytes32 position = DIAMOND_STORAGE_POSITION;
        assembly {
            ds.slot := position
        }
    }

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    function setContractOwner(address _newOwner) internal {
        DiamondStorage storage ds = diamondStorage();
        address previousOwner = ds.contractOwner;
        ds.contractOwner = _newOwner;
        emit OwnershipTransferred(previousOwner, _newOwner);
    }

    function contractOwner() internal view returns (address contractOwner_) {
        contractOwner_ = diamondStorage().contractOwner;
    }

    function enforceIsContractOwner() internal view {
        if (msg.sender != diamondStorage().contractOwner) {
            revert NotContractOwner(msg.sender, diamondStorage().contractOwner);
        }
    }

    event DiamondCut(IDiamondCut.FacetCut[] _diamondCut, address _init, bytes _calldata);

    // Internal function version of diamondCut
    function diamondCut(IDiamondCut.FacetCut[] memory _diamondCut, address _init, bytes memory _calldata) internal {
        for (uint256 facetIndex; facetIndex < _diamondCut.length; facetIndex++) {
            bytes4[] memory functionSelectors = _diamondCut[facetIndex].functionSelectors;
            address facetAddress = _diamondCut[facetIndex].facetAddress;
            if (functionSelectors.length == 0) {
                revert NoSelectorsProvidedForFacetForCut(facetAddress);
            }
            IDiamondCut.FacetCutAction action = _diamondCut[facetIndex].action;
            if (action == IDiamond.FacetCutAction.Add) {
                addFunctions(facetAddress, functionSelectors);
            } else if (action == IDiamond.FacetCutAction.Replace) {
                replaceFunctions(facetAddress, functionSelectors);
            } else if (action == IDiamond.FacetCutAction.Remove) {
                removeFunctions(facetAddress, functionSelectors);
            } else {
                revert IncorrectFacetCutAction(uint8(action));
            }
        }
        emit DiamondCut(_diamondCut, _init, _calldata);
        initializeDiamondCut(_init, _calldata);
    }

    function addFunctions(address _facetAddress, bytes4[] memory _functionSelectors) internal {
        if (_facetAddress == address(0)) {
            revert CannotAddSelectorsToZeroAddress(_functionSelectors);
        }
        DiamondStorage storage ds = diamondStorage();
        uint16 selectorCount = uint16(ds.selectors.length);
        enforceHasContractCode(_facetAddress, "LibDiamondCut: Add facet has no code");
        for (uint256 selectorIndex; selectorIndex < _functionSelectors.length; selectorIndex++) {
            bytes4 selector = _functionSelectors[selectorIndex];
            address oldFacetAddress = ds.facetAddressAndSelectorPosition[selector].facetAddress;
            if (oldFacetAddress != address(0)) {
                revert CannotAddFunctionToDiamondThatAlreadyExists(selector);
            }
            ds.facetAddressAndSelectorPosition[selector] = FacetAddressAndSelectorPosition(
                _facetAddress,
                selectorCount
            );
            ds.selectors.push(selector);
            selectorCount++;
        }
    }

    function replaceFunctions(address _facetAddress, bytes4[] memory _functionSelectors) internal {
        DiamondStorage storage ds = diamondStorage();
        if (_facetAddress == address(0)) {
            revert CannotReplaceFunctionsFromFacetWithZeroAddress(_functionSelectors);
        }
        enforceHasContractCode(_facetAddress, "LibDiamondCut: Replace facet has no code");
        for (uint256 selectorIndex; selectorIndex < _functionSelectors.length; selectorIndex++) {
            bytes4 selector = _functionSelectors[selectorIndex];
            address oldFacetAddress = ds.facetAddressAndSelectorPosition[selector].facetAddress;
            // can't replace immutable functions -- functions defined directly in the diamond in this case
            if (oldFacetAddress == address(this)) {
                revert CannotReplaceImmutableFunction(selector);
            }
            if (oldFacetAddress == _facetAddress) {
                revert CannotReplaceFunctionWithTheSameFunctionFromTheSameFacet(selector);
            }
            if (oldFacetAddress == address(0)) {
                revert CannotReplaceFunctionThatDoesNotExists(selector);
            }
            // replace old facet address
            ds.facetAddressAndSelectorPosition[selector].facetAddress = _facetAddress;
        }
    }

    function removeFunctions(address _facetAddress, bytes4[] memory _functionSelectors) internal {
        DiamondStorage storage ds = diamondStorage();
        uint256 selectorCount = ds.selectors.length;
        if (_facetAddress != address(0)) {
            revert RemoveFacetAddressMustBeZeroAddress(_facetAddress);
        }
        for (uint256 selectorIndex; selectorIndex < _functionSelectors.length; selectorIndex++) {
            bytes4 selector = _functionSelectors[selectorIndex];
            FacetAddressAndSelectorPosition memory oldFacetAddressAndSelectorPosition = ds
                .facetAddressAndSelectorPosition[selector];
            if (oldFacetAddressAndSelectorPosition.facetAddress == address(0)) {
                revert CannotRemoveFunctionThatDoesNotExist(selector);
            }

            // can't remove immutable functions -- functions defined directly in the diamond
            if (oldFacetAddressAndSelectorPosition.facetAddress == address(this)) {
                revert CannotRemoveImmutableFunction(selector);
            }
            // replace selector with last selector
            selectorCount--;
            if (oldFacetAddressAndSelectorPosition.selectorPosition != selectorCount) {
                bytes4 lastSelector = ds.selectors[selectorCount];
                ds.selectors[oldFacetAddressAndSelectorPosition.selectorPosition] = lastSelector;
                ds.facetAddressAndSelectorPosition[lastSelector].selectorPosition = oldFacetAddressAndSelectorPosition
                    .selectorPosition;
            }
            // delete last selector
            ds.selectors.pop();
            delete ds.facetAddressAndSelectorPosition[selector];
        }
    }

    function initializeDiamondCut(address _init, bytes memory _calldata) internal {
        if (_init == address(0)) {
            return;
        }
        enforceHasContractCode(_init, "LibDiamondCut: _init address has no code");
        (bool success, bytes memory error) = _init.delegatecall(_calldata);
        if (!success) {
            if (error.length > 0) {
                // bubble up error
                /// @solidity memory-safe-assembly
                assembly {
                    let returndata_size := mload(error)
                    revert(add(32, error), returndata_size)
                }
            } else {
                revert InitializationFunctionReverted(_init, _calldata);
            }
        }
    }

    function enforceHasContractCode(address _contract, string memory _errorMessage) internal view {
        uint256 contractSize;
        assembly {
            contractSize := extcodesize(_contract)
        }
        if (contractSize == 0) {
            revert NoBytecodeAtAddress(_contract, _errorMessage);
        }
    }
}

contracts/storage/RoleAccessControl.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "../utils/Errors.sol";

library RoleAccessControl {
  using EnumerableSet for EnumerableSet.Bytes32Set;

  bytes32 internal constant ACCESS_CONTROL_KEY = keccak256(abi.encode("penx.io.storage.AccessControl"));

  bytes32 constant ROLE_ADMIN = "ADMIN";
  bytes32 constant ROLE_CONFIG = "CONFIG";
  bytes32 constant ROLE_KEEPER = "KEEPER";

  struct Store {
    mapping(address => EnumerableSet.Bytes32Set) accountRoles;
  }

  function load() public pure returns (Store storage self) {
    bytes32 s = ACCESS_CONTROL_KEY;
    assembly {
      self.slot := s
    }
  }

  function checkRole(bytes32 role) internal view {
    if (!hasRole(msg.sender, role)) {
      revert Errors.InvalidRoleAccess(msg.sender, role);
    }
  }

  function hasRole(bytes32 role) internal view returns (bool) {
    return hasRole(msg.sender, role);
  }

  function hasRole(address account, bytes32 role) internal view returns (bool) {
    Store storage self = load();
    return self.accountRoles[account].contains(role);
  }

  function grantRole(address account, bytes32 role) internal {
    Store storage self = load();
    self.accountRoles[account].add(role);
  }

  function revokeRole(address account, bytes32 role) internal {
    Store storage self = load();
    if (self.accountRoles[account].contains(role)) {
      self.accountRoles[account].remove(role);
    }
  }

  function revokeAllRole(address account) internal {
    Store storage self = load();
    delete self.accountRoles[account];
  }
}

contracts/storage/UuidCreator.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

library UuidCreator {
  bytes32 private constant _UUID_CREATOR = keccak256(abi.encode("penx.io.storage.UuidCreator"));

  uint256 private constant MIN_ID = 1111;

  struct Store {
    mapping(bytes32 => uint256) lastIds;
  }

  function load() public pure returns (Store storage self) {
    bytes32 s = _UUID_CREATOR;

    assembly {
      self.slot := s
    }
  }

  function nextId(bytes32 key) external returns (uint256) {
    Store storage self = load();
    uint256 lastId = self.lastIds[key];
    if (lastId < MIN_ID) {
      lastId = MIN_ID + 1;
    } else {
      lastId++;
    }
    self.lastIds[key] = lastId;
    return lastId;
  }

  function getId(bytes32 key) external view returns (uint256) {
    Store storage self = load();
    return self.lastIds[key];
  }
}

contracts/tokens/BelieverNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "hardhat/console.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "../vault/DaoVault.sol";

contract BelieverNFT is ERC721, ERC721URIStorage {
  uint256 private constant MAX_SUPPLY = 1024;
  uint256 private constant MIN_PRICE = 0.1 ether;
  uint256 private constant MAX_PRICE = 3 ether;
  uint256 private constant PRICE_INCREMENT = (MAX_PRICE - MIN_PRICE) / MAX_SUPPLY;

  uint256 private currentSupply = 0;

  uint256 private constant referrerRewardRate = 10;
  uint256 private constant referralsDiscountRate = 10;

  mapping(address => string) private codes;

  mapping(string => User) private users;

  struct User {
    address referrer;
    address[] referrals;
  }

  struct BelieverNFTInfo {
    uint256 maxSupply;
    uint256 currentSupply;
    uint256 currentPrice;
    uint256 minPrice;
    uint256 maxPrice;
  }

  DaoVault private immutable daoVault;

  error ReferralCodeExisted(address account, string code);
  error CannotUseOwnCode(address account, string code);

  event NFTMinted(address indexed owner, uint256 indexed tokenId);

  constructor(DaoVault _daoVault) ERC721("PenX Believer NFT", "PXB") {
    daoVault = _daoVault;
  }

  function mintNFT(string calldata code) external payable {
    require(currentSupply < MAX_SUPPLY, "Maximum supply reached");
    require(msg.value >= getCurrentPrice(), "Insufficient payment");

    uint256 tokenId = currentSupply + 1;

    string memory uri = getTokenURI(tokenId);

    _safeMint(msg.sender, tokenId);
    _setTokenURI(tokenId, uri);

    currentSupply++;

    address account = getReferrer(code);

    if (account == msg.sender) {
      revert CannotUseOwnCode(msg.sender, code);
    }

    emit NFTMinted(msg.sender, tokenId);

    // To daoVault directly
    if (account == address(0)) {
      payable(address(daoVault)).transfer(msg.value);
      return;
    }

    uint256 rewardAmount = (msg.value * referrerRewardRate) / 100;
    uint256 discountAmount = (msg.value * referralsDiscountRate) / 100;
    uint256 daoAmount = msg.value - rewardAmount - discountAmount;

    payable(account).transfer(rewardAmount);
    payable(msg.sender).transfer(discountAmount);
    payable(address(daoVault)).transfer(daoAmount);
  }

  function tokenURI(uint256 tokenId) public view override(ERC721, ERC721URIStorage) returns (string memory) {
    return super.tokenURI(tokenId);
  }

  function supportsInterface(bytes4 interfaceId) public view override(ERC721, ERC721URIStorage) returns (bool) {
    return super.supportsInterface(interfaceId);
  }

  function getCurrentPrice() public view returns (uint256) {
    uint256 currentPrice = MIN_PRICE + (currentSupply * PRICE_INCREMENT);
    return currentPrice > MAX_PRICE ? MAX_PRICE : currentPrice;
  }

  function getTokenInfo() public view returns (BelieverNFTInfo memory info) {
    return BelieverNFTInfo(MAX_SUPPLY, currentSupply, getCurrentPrice(), MIN_PRICE, MAX_PRICE);
  }

  function getReferrer(string calldata code) public view returns (address) {
    return users[code].referrer;
  }

  function getReferrals(address account) public view returns (address[] memory) {
    string memory code = codes[account];
    return users[code].referrals;
  }

  function getReferralCode(address account) external view returns (string memory) {
    return codes[account];
  }

  function setReferralCode(string calldata code) external {
    uint length = bytes(code).length;

    require(length > 3, "Code length should greater then 3");

    address account = getReferrer(code);

    if (account != address(0)) {
      revert ReferralCodeExisted(msg.sender, code);
    }

    users[code].referrer = msg.sender;
    codes[msg.sender] = code;
  }

  function getTokenURI(uint256 tokenId) internal pure returns (string memory) {
    string memory baseURI = "https://www.penx.io/api/believer-nft/";

    return string(abi.encodePacked(baseURI, Strings.toString(uint256(tokenId))));
  }
}

