// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import { UD60x18, ud, add, mul, ln } from "@prb/math/src/UD60x18.sol";

contract PenXKey is ERC721, ERC721URIStorage, ERC721Enumerable, Ownable {
  /// @notice Current supply of minted NFTs
  uint256 public currentSupply;

  string public baseURI;

  event NFTMinted(address indexed owner, uint256 indexed tokenId);

  constructor(address initialOwner, string memory _baseURI) ERC721("PenX Key", "PXK") Ownable(initialOwner) {
    baseURI = _baseURI;
  }

  function mintKeys(uint256 numberOfKeys) external onlyOwner {
    for (uint256 i = 0; i < numberOfKeys; i++) {
      _mintNFT();
    }
  }

  function tokenURI(uint256 tokenId) public view override(ERC721, ERC721URIStorage) returns (string memory) {
    return super.tokenURI(tokenId);
  }

  function _getTokenURI(uint256 tokenId) internal view returns (string memory) {
    string memory postfix = ".json";
    return string(abi.encodePacked(baseURI, Strings.toString(uint256(tokenId)), postfix));
  }

  function _mintNFT() internal {
    currentSupply++;

    uint256 tokenId = currentSupply;
    string memory uri = _getTokenURI(tokenId);

    _safeMint(msg.sender, tokenId);
    _setTokenURI(tokenId, uri);
    emit NFTMinted(msg.sender, tokenId);
  }

  function _calculatePrice(uint256 x) internal pure returns (uint256) {
    UD60x18 fixedX = ud(x);

    UD60x18 term = ud(1020000000000000).mul(fixedX).add(ud(1e18));

    UD60x18 lnTerm = ln(term);

    UD60x18 result = lnTerm.add(ud(102400000000000000));

    return result.unwrap();
  }

  function supportsInterface(
    bytes4 interfaceId
  ) public view override(ERC721, ERC721URIStorage, ERC721Enumerable) returns (bool) {
    return super.supportsInterface(interfaceId);
  }

  function _update(
    address to,
    uint256 tokenId,
    address auth
  ) internal override(ERC721, ERC721Enumerable) returns (address) {
    return super._update(to, tokenId, auth);
  }

  function _increaseBalance(address account, uint128 value) internal override(ERC721, ERC721Enumerable) {
    super._increaseBalance(account, value);
  }
}

